import React, { useEffect, useRef } from "react";
import "./App.css";
import WebGLApp from "./webgl-app/webgl-app";

function App() {
  const rootRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    // Initialize WebGLApp and append the WebGLRenderer canvas to rootRef.current
    if (rootRef.current) {
      const webglapp = new WebGLApp(rootRef.current);
      rootRef.current.appendChild(webglapp.renderer.domElement);
    }
  }, []);

  return <div ref={rootRef}></div>;
}

export default App;
